import React, { useEffect, useState } from "react";
import "./TTT.css";

const initialMatrix = [
  ["", "", ""],
  ["", "", ""],
  ["", "", ""],
];
function TTT() {
  const [matrix, setMatrix] = useState(
    JSON.parse(localStorage.getItem("Matrix")) || initialMatrix
  );
  const [value, setValue] = useState(localStorage.getItem("Value") || "x");
  const [winner, setWinner] = useState(localStorage.getItem("Winner") || "");
  const [count, setCount] = useState(
    JSON.parse(localStorage.getItem("Count")) || 0
  );
  const [winningLine, setWinningLine] = useState(
    JSON.parse(localStorage.getItem("WinningLine")) || []
  );
  const [line, setLine] = useState(localStorage.getItem("Line") || "");

  const [xWinning, setXwinning] = useState(
    JSON.parse(localStorage.getItem("xWinning")) || 0
  );
  const [oWinning, setOwinning] = useState(
    JSON.parse(localStorage.getItem("oWinning")) || 0
  );

  useEffect(() => {
    localStorage.setItem("Matrix", JSON.stringify(matrix));
  }, [matrix]);

  useEffect(() => {
    localStorage.setItem("Value", value);
  }, [value]);

  useEffect(() => {
    localStorage.setItem("Winner", winner);
  }, [winner]);

  useEffect(() => {
    localStorage.setItem("Count", count);
  }, [count]);

  useEffect(() => {
    localStorage.setItem("WinningLine", JSON.stringify(winningLine));
  }, [winningLine]);

  useEffect(() => {
    localStorage.setItem("Line", line);
  }, [line]);

  useEffect(() => {
    localStorage.setItem("xWinning", xWinning);
  }, [xWinning]);

  useEffect(() => {
    localStorage.setItem("oWinning", oWinning);
  }, [oWinning]);

  const xOchange = (r, i) => {
    const dup_arrys = [...matrix];
    if (dup_arrys[r][i]) {
      return;
    } else {
      dup_arrys[r][i] = value;
      setValue(value === "x" ? "o" : "x");
      setMatrix(dup_arrys);
      cheacking();
      setCount(count + 1);
    }
  };

  const points = (newWinner) => {
    if (newWinner === "x") {
      setXwinning(xWinning + 1);
    }
    if (newWinner === "o") {
      setOwinning(oWinning + 1);
    }
  };

  const checkLine = (r1, c1, r2, c2, r3, c3) => {
    // console.log(r1, c1, r2, c2, r3, c3);
    return (
      matrix[r1][c1] === matrix[r2][c2] &&
      matrix[r2][c2] === matrix[r3][c3] &&
      matrix[r3][c3] !== ""
    );
  };

  const cheacking = async () => {
    // Rows and Columns
    for (let i = 0; i < 3; i++) {
      if (checkLine(i, 0, i, 1, i, 2)) {
        const newWinner = matrix[i][0];
        setWinner(newWinner);
        setLine("colrow");
        setWinningLine([
          [i, 0],
          [i, 1],
          [i, 2],
        ]);
        points(newWinner);
        return;
      }
      if (checkLine(0, i, 1, i, 2, i)) {
        const newWinner = matrix[0][i];
        setWinner(newWinner);
        points(newWinner);
        setLine("colcol");
        setWinningLine([
          [0, i],
          [1, i],
          [2, i],
        ]);
        return;
      }
    }

    // Diagonals
    if (checkLine(0, 0, 1, 1, 2, 2)) {
      const newWinner = matrix[0][0];
      setWinner(newWinner);
      points(newWinner);
      setLine("colright");
      setWinningLine([
        [0, 0],
        [1, 1],
        [2, 2],
      ]);
      return;
    }
    if (checkLine(0, 2, 1, 1, 2, 0)) {
      const newWinner = matrix[0][2];
      setWinner(newWinner);
      points(newWinner);
      setLine("colleft");
      setWinningLine([
        [0, 2],
        [1, 1],
        [2, 0],
      ]);
      return;
    }

    // // Draw
    // if (count === 9 && winner === "") {
    //   setWinner("DRAW");
    // }
  };

  return (
    <div>
      <h1>TIC TAC TOE </h1>
      <div className=" box">
        <div className="win">
          <div>
            {count === 9 && winner === "" ? (
              <h2>The Match is = " DRAW "</h2>
            ) : (
              <h2>
                {winner === ""
                  ? `Your Turn "${value}"`
                  : `The Winner is = " ${winner} " `}
              </h2>
            )}
          </div>
          <div>
            <h2>
              <span style={{ color: "#ff7700" }}>X [ {xWinning} ] </span> :
              <span style={{ color: "#ff0000" }}> O [ {oWinning} ]</span>
            </h2>
          </div>
        </div>

        {matrix.map((data, rindex) => (
          <div className="row">
            {data.map((data, cindex) => (
              <div
                className={`col  


                ${
                  matrix[rindex][cindex] === "" &&
                  winner === "" &&
                  `col${value}`
                }

                ${matrix[rindex][cindex] === "x" && "black"} ${
                  matrix[rindex][cindex] === "o" && "white"
                }
                 ${
                   winningLine.some(
                     ([row, col]) => row === rindex && col === cindex
                   ) && `${line}`
                 }`}
                onClick={() => {
                  winner === "" && xOchange(rindex, cindex);
                }}
              >
                {matrix[rindex][cindex]}
              </div>
            ))}
          </div>
        ))}
      </div>
      <div className="btn ">
        <button
          style={{ marginRight: "20px" }}
          onClick={() => {
            setMatrix([
              ["", "", ""],
              ["", "", ""],
              ["", "", ""],
            ]);
            setValue("x");
            setWinner("");
            setCount(0);
            setWinningLine([]);
            setLine("");
          }}
        >
          PLAY AGAIN
        </button>
        <button
          onClick={() => {
            setMatrix([
              ["", "", ""],
              ["", "", ""],
              ["", "", ""],
            ]);
            setValue("x");
            setWinner("");
            setCount(0);
            setWinningLine([]);
            setLine("");
            setOwinning(0);
            setXwinning(0);
            alert("Are you sure you want to reset the game?");
          }}
        >
          RESTART
        </button>
      </div>
    </div>
  );
}

export default TTT;
